mapper.select:
select
    BOARD_NO,
    SUBJECT,
    WRITER,
    CONTENT,
    TO_CHAR(TO_DATE(REG_DATE,'YYYYMMDDHHMI'),'YYYY-MM-DD HH24:MI') AS REG_DATE,
    SECRET_YN
from
    BOARD_NEXACRO

	
		AND BOARD_NO = #{board_no}
	
	
		AND SUBJECT = #{subject}
	
	
		AND CONTENT = #{content}
	
	
		AND WRITER = #{writer}
	
	
		AND REG_DATE = #{reg_date}
	
	
		AND SECRET_YN = #{secret_yn}
	
	

	ORDER BY BOARD_NO

mapper.update:
UPDATE 
BOARD_NEXACRO
SET CONTENT=#{content}
WHERE BOARD_NO=${board_no}
